$(document).ready(function(){$("#birthday").on("change",function(t){var a=$("#birthday").val(),e=new Date(a),r=new Date,n=r.getTime()-e.getTime(),i=new Date(n),v=i.getFullYear()-1970;$("#age").val(v)})});$(function(){var t=new Date,a=t.getMonth()+1,e=t.getDate(),r=t.getFullYear();a<10&&(a="0"+a.toString()),e<10&&(e="0"+e.toString());var n=r+"-"+a+"-"+e;$("#birthday").attr("max",n)});
